var INLINE_LINK=/(\S+)(?:\s+(.+))?/,log=require("winston");module.exports={name:"link",description:"Process inline link tags (of the form {@link some/uri Some Title}), replacing them with HTML anchors",handlerFactory:function(e,t){return function(r,i,s){var o=t.get("versionData");return s.replace(INLINE_LINK,function(t,n,i){var s=e.getLink(n,i,r);return s.valid||(log.warn(s.error,"in",r.relativePath+"#"+r.name),s.title="TODO:"+s.title),'<a href="'+o.current.href+"/"+s.url+'">'+s.title+"</a>"})}}}